import React from 'react';
import BuildQuestion from "./buildQuestion";
import QuizIntro from './QuizIntro';
import Button from "react-bootstrap/Button";
import ServerConnection from "../Main Page/Api Connection/serverConnection";


class BuildQuiz extends React.Component {
    constructor(props){
        super (props);
        this.user = this.props.user;
        this.state = {
            questionsObj: {},
            quizName: '',
            questionAmount: '',
            counter: 1}
        this.addQuestionObj = this.addQuestionObj.bind(this);
        this.checkQuizIntro = this.checkQuizIntro.bind(this);
        this.checkQuestionAmount = this.checkQuestionAmount.bind(this);
        this.setQuestionAmount = this.setQuestionAmount.bind(this);
        this.setQuizName = this.setQuizName.bind(this);
        this.incrementCounter = this.incrementCounter.bind(this);

        this.wholeQuizObj={}
    }

    addQuestionObj(questionObj) {
        this.questionsObj[questionObj.keys()] = questionObj.values();
        this.setState({counter: this.counter++});
    }

    checkQuizIntro() {
        return !(this.props.quizName && this.props.questionAmount);
    }

    setQuizName(quizName) {
        this.props.setQuizName(quizName);

    }

    setQuestionAmount(questionAmount) {
        this.props.setQuestionAmount(questionAmount);
    }


    checkQuestionAmount() {
        return this.state.questionAmount > this.state.questionsObj.length;
    }

    async sendQuizObj() {
        const response = await ServerConnection.postQuiz(this.state.quizName, this.user, this.state.questionsObj).then(response => response)
        alert(response)

    }

    incrementCounter() {
        let counter = this.state.counter;
        this.setState({counter: counter++})
    }

    render() {
        return(<div className="Quiz">
            {this.checkQuizIntro ? <QuizIntro
                setQuizName={this.setQuizName}
                setQuestionAmount={this.setQuestionAmount}/>: null}
            {this.checkQuestionAmount && !this.checkQuizIntro ? <BuildQuestion
                addQuestionObj={this.addQuestionObj}
                questionNumber={this.state.counter}
                incrementCounter={this.incrementCounter}/> : null}
            {!this.checkQuestionAmount ? <Button block size="lg" type="submit" onClick={this.sendQuizObj}>Submit Quiz</Button>: null}
        </div>)
    }

}

export default BuildQuiz;
