import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Navigation, Footer, Home, Login, Register, BuildQuiz, BuildQuestion} from "./components/index";
import './App.css';





class App extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {user: '',
            jwtToken: '',
            quizBuilt: false,
            quiz: {quizName: '',
                questionAmount: '',
                questionsObj: {}}};
        this.setToken = this.setToken.bind(this);
        this.setUser = this.setUser.bind(this);
        this.setOriginalQuiz.bind(this);
        this.setQuizIntro = this.setQuizIntro.bind(this);
        this.setQuizName = this.setQuizName.bind(this);
        this.setQuestionsObj = this.setQuestionsObj.bind(this);

    }
    setToken(jwtToken) {
        this.setState( {jwtToken: jwtToken})
    }

    setUser(user) {
        this.setState( {user: user})
    }

    setOriginalQuiz() {
        this.setQuizName('')
        this.setQuizIntro('')
        this.setQuestionsObj({});
    }

    setQuizName(quizName) {
        this.setState({quiz: { ...this.state.quiz, quizName: quizName}})
    }

    setQuizIntro(quizName, questionAmount) {
        this.setState({quiz: { ...this.state.quiz, quizName: quizName, questionAmount: questionAmount}})
    }

    setQuestionsObj(questionsObj) {
        this.setState({quiz: { ...this.state.quiz, questionsObj: questionsObj}})
    }




    render() {
        return (
            <div className="App">
                <Router>
                    <Navigation />
                    <Switch>
                        <Route path="/" exact component={() => <Home />} />
                        <div className="login">
                            <Route path="/login" >
                            <Login setToken={this.setToken} setUser={this.setUser}/>
                            </Route>
                            <Route path="/signup" exact component={() => <Register setToken={this.setToken} setUser={this.setUser}/>} />
                            <Route path="/buildQuiz" exact component={() => <BuildQuiz user={this.state.user}
                                                                                       setQuizIntro={this.setQuizIntro} setQuestionsObj={this.setQuestionsObj}
                                                                                       setOriginalQuiz={this.setOriginalQuiz}/>}
                                quizName ={this.state.quizName} questionAmount={this.state.questionAmount}/>
                        </div>

                    </Switch>
                    {/*<Footer />*/}
                </Router>
            </div>
        )
    }
}


export default App;
