import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Navigation, Footer, Home, Login, Register, BuildQuiz, BuildQuestion} from "./components/index";
import './App.css';
import DoQuiz from "./components/Do Quiz/DoQuiz";




class App extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {user: 'yoav',
            jwtToken: '',
            quiz: ''}
        this.setToken = this.setToken.bind(this);
        this.setUser = this.setUser.bind(this);

    }

    shouldComponentUpdate(nextProps, nextState, nextContext) {
        return false
    }

    setToken(jwtToken) {
        this.setState( {jwtToken: jwtToken})
    }

    setUser(user) {
        this.setState( {user: user})
    }


    render() {
        return (
            <div className="App">
                <Router>
                    <Navigation />
                    <Switch>
                        <Route path="/" exact component={() => <Home />} />
                        <div className="login">
                            <Route path="/login" >
                            <Login setToken={this.setToken} setUser={this.setUser}/>
                            </Route>
                            <Route path="/signup" exact component={() => <Register setToken={this.setToken} setUser={this.setUser}/>} />
                            <Route path="/buildQuiz" exact component={() => <BuildQuiz user={this.state.user} jwtToken={this.state.jwtToken} quiz={this.state.quiz}/>} />
                            <Route path="/doQuiz" exact component={() => <DoQuiz user={this.state.user}/>} />

                        </div>

                    </Switch>
                    {/*<Footer />*/}
                </Router>
            </div>
        )
    }
}


export default App;
