import React from 'react';
import ServerConnection from "../Main Page/Api Connection/serverConnection";
import DoQuestion from "./DoQuestion";
import Button from "react-bootstrap/Button";
import HomeButton from "../Sign Up/returnToHomePage";

class DoQuiz extends React.Component {
    constructor(props) {
        super(props)
        this.questionList = [];
        this.userAnswers = []

    }

    async componentDidMount() {
        this.getQuiz = this.getQuiz.bind(this);
        this.getScore = this.getScore.bind(this);
        this.questionList = await this.getQuiz();

    }

    async getQuiz() {
       this.questionList = await ServerConnection.getQuiz(this.props.quiz, this.props.jwtToken).then(response => response);
    }

    addAnswer(answer) {
        this.userAnswers.push(answer);
    }

    showResult() {
        return this.userAnswers.length < this.questionList.length;
    }

    getScore() {
        return this.userAnswers * 100 / this.questionList.length;
    }

    async updatePercentage() {
        const percentage = this.getScore();
        return await ServerConnection.updatePercentage(this.props.quiz, percentage, this.props.user)
    }

     render() {
        let questionsList = this.questionList.map(question => <DoQuestion question={question}/>)
        let quizShow;
        if (this.showResult()) {
            quizShow =  {questionsList}
        } else {
            quizShow =  (
                <div className="score-section">
                    You scored {this.getScore()} out of 100!
                    submitButton = <Button block size="lg" type="submit" onClick={this.updatePercentage.bind(this)}>Submit Quiz</Button>
                    <HomeButton/>
                </div>
            )
        }
        return (<div className="quiz">
            {quizShow}
        </div>

        )
    }
}

export default DoQuiz;
